import React, { useState } from "react";

const ColorBox = () => {
  const [color, setColor] = useState("deeppink");

  const handleBoxClick = () => {
    setColor("blue");
  };

  return (
    <div>
      <div
        className="color-box"
        style={{ backgroundColor: color }}
        onClick={() => handleBoxClick()}
      >
        COLOR BOX
      </div>
    </div>
  );
};

export default ColorBox;
